/**
* dependencias a utilizar
*/
import { Component, OnInit, Input } from '@angular/core';
import { Router } from '@angular/router';
import { ListarComponent } from '../../../crud/listar.component';
import { CrudService } from '../../../crud/crud.service';
import { formatoHtmlPipe } from '../../../pipes/formatoHtml.pipe';

import { SecurityContext} from '@angular/core';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';

import { environment } from '../../../../environments/environment';


import * as jsPDF from 'jspdf';
import * as autoTable from 'jspdf-autotable';

import * as moment from 'moment';

import { forEach } from '@angular/router/src/utils/collection';
import { concat } from 'rxjs/operators/concat';


/**
* selector si se desea ocupar en un HTML
* y su archivo HTML
*/
@Component({
  selector: 'incidencias-lista',
  templateUrl: './lista.component.html',
  //template: `[innerHTML]='texto'`,
  styleUrls: ['./lista.component.css'],
  providers: [formatoHtmlPipe]
})


/**
* Esta clase inicializa la lista del componente
* con los datos que se requieran.
*/
export class ListaComponent {

    /**
    * Contiene la variable para comunicarse con los metodos del Crud listar.component.ts.
    * @type {any}
    */
    @Input() ctrl: any; 
    
    /**
    * Contiene el tamaño del cuerpo de la seccion donde esten los controles en la vista.
    * @type {any}
    */
    tamano = document.body.clientHeight;
    
    /**
    * Contiene la variable que realiza el binding para buscar el estado de incidencia.
    * @type {any}
    */
    edo_incidencia: any = '';

    /**
    * Contiene la nueva url.
    * @type {string}
    */
    url_nuevo: string = '';
    
    /**
    * Contiene los permisos del LocalStorage.
    * @type {any}
    */
    permisos = JSON.parse(localStorage.getItem("permisos"));
    
    /**
    * Contiene la unidad medica que esta en el LocalStorage.
    * @type {any}
    */
    clues = JSON.parse(localStorage.getItem("clues"));
    
    /**
    * se almacena la posicion de la url.
    * @type {any}
    */  
    carpeta;

    /**
    * almacena la posicion de la url del modulo.
    * @type {any}
    */  
    modulo;

    /**
    * almacena el controlador que se este trabajando.
    * @type {any}
    */ 
    controlador;
    
    /**
    * almacena el modulo actual.
    * @type {any}
    */ 
    modulo_actual;
    
    /**
    * obtiene el icono del modulo.
    * @type {any}
    */ 
    icono;
    
    /**
    * array que almacena los datos del paciente a mostrar en el PDF.
    * @type {any}
    */ 
    datosSeguimiento:any = [];

    /**
    * almacena las imagenes cargadas de acuerdo a una referencia nueva.
    * @type {any}
    */
    public url_img_referencias: string = `${environment.API_PATH}/public/adjunto/referencias/`;


    /**
    * almacena las imagenes cargadas de acuerdo a una alta nueva.
    * @type {any}
    */
    public url_img_altas: string = `${environment.API_PATH}/adjunto/contrareferencias/`;

  
  /**
  * Este método inicializa la carga de las dependencias 
  * que se necesitan para el funcionamiento del catalogo
  */
  constructor(
              public crudService: CrudService,
              public formatohtml: formatoHtmlPipe,
              private _sanitizer: DomSanitizer 
            ){ }
  /**
  * Este método inicializa la carga de la vista asociada
  * @return void
  */
  ngOnInit() {

    this.clues;
    var url = location.href.split("/");
    this.carpeta = url[4];
    this.modulo = url[5];


    //this.modulo_actual = this.modulo.replace(/(?:^|\s)\S/g, function(a) { return a.toUpperCase(); }).replace(/[-_]+/g, ' ');

    var ctrl = "-" + this.modulo;
    this.controlador = ctrl.toLowerCase()
        // remplazar _ o - por espacios
        .replace(/[-_]+/g, ' ')
        // quitar numeros
        .replace(/[^\w\s]/g, '')
        // cambiar a mayusculas el primer caracter despues de un espacio
        .replace(/ (.)/g, function($1) {
            return $1.toUpperCase(); })
        // quitar espacios y agregar controller
        .replace(/ /g, '') + "Controller";
    
    this.url_nuevo = '/' + this.carpeta + '/' + this.modulo + '/nuevo';
    var titulo_icono = this.obtener_icono(url[5] + '/' + url[6], JSON.parse(localStorage.getItem("menu")));
    this.permisos;
    this.icono = titulo_icono.icono;
    this.modulo_actual = titulo_icono.titulo;

  }

  /**
  * Este método obtiene el icono del modulo con el que se este trabajando
  * @return {any} el icono del modulo con el que se este trabajando
  */
  obtener_icono(controlador, menu) {
    menu.map((element, key) => {
      if (typeof this.icono == 'undefined') {
        if (element.lista) {
          this.icono = this.obtener_icono(controlador, element.lista);
          return this.icono;
        }
        if (element.path.indexOf(controlador) > -1) {
          this.icono = element;
          return this.icono;
        }
      }
      else
        return this.icono;
    });
    return this.icono;
  }  
  
  /**
  * Este cambia la busqueda de acuerdo al estado de incidencia que se este buscando
  * @return void
  */
  cambiar_busqueda(){
    setTimeout(() => {
      document.getElementById("rebuscar").click();
    }, 100);
  }

  /**
  * Este genera la consulta de acuerdo al folio de incidencia que se este buscando
  * para luego ejecutar la función de crear el PDF del Historial de la paciente.
  * @return void
  */
  generate(e, id) {

        this.crudService.ver(id, "incidencias").subscribe(
            resultado => {            

              this.datosSeguimiento = resultado.data;
              
              setTimeout(() => {

                this.pdf();
                
              }, 200);

              error => {

              }
            },
        );
  }

  /**
  * Este genera el PDF de acuerdo a la consulta en el metodo generate().
  * @return void
  */
  pdf(){

    let jsPDF = require('jspdf');
    require('jspdf-autotable');

    var doc = new jsPDF('l');

    
    var img = {
      header: 'data:image/jpeg;base64,'
    }

      var paciente = doc.autoTableHtmlToJson(document.getElementById("datosPaciente"));

      var seguimientos = doc.autoTableHtmlToJson(document.getElementById("datosSeguimiento"));

      var referencias = doc.autoTableHtmlToJson(document.getElementById("datosReferencias"));

      var altas = doc.autoTableHtmlToJson(document.getElementById("datosAlta"));
    
      var totalPagesExp = "{total_pages_count_string}";

      var pageContent = function (data) {
        // HEADER
        doc.setFontSize(20);
        doc.setTextColor(40);
        doc.setFontStyle('normal');

        if (img.header ) {
          doc.addImage(img.header, 'JPEG', 30, 12, 240, 18);
          // doc.addImage(img.base64Img2, 'JPEG', 10, 10, 60, 20);
          // doc.addImage(img.base64Img3, 'JPEG', 97, 3, 15, 15);
          // doc.addImage(img.base64Img3, 'JPEG', 194, 3, 15, 15);
          
        }

        doc.text("Información de Paciente", data.settings.margin.left + 110, 9);
        doc.setFontSize(8)
        doc.setFont('helvetica')
        doc.text("Sistema para la Gestión de Incidencias en Salud (SGiS)", data.settings.margin.left + 111, 13);

        // FOOTER
        var str = "Pagina " + data.pageCount;
        // Total page number plugin only available in jspdf v1.0+
        if (typeof doc.putTotalPages === 'function') {
            str = str + " de " + totalPagesExp;
        }
        doc.setFontSize(10);
        doc.setFontType("italic");
        doc.text(str, data.settings.margin.left, doc.internal.pageSize.height - 10);
    };

      doc.text("Información de la Paciente", 10, 38);
      doc.setFont('helvetica')

      doc.autoTable(paciente.columns, paciente.data, {

        startY: 41,
        margin: {top: 30, horizontal: 7},
        styles: {overflow: 'linebreak',columnWidth: 'wrap', fontSize: 8},
        columnStyles: {text: {columnWidth: 'auto'}},
      });

      let first = doc.autoTable.previous;

      doc.text("Seguimientos", 10, first.finalY + 10);
      doc.setFont('helvetica')

      doc.autoTable(seguimientos.columns, seguimientos.data, {
        
        startY: first.finalY + 15,
        margin: {top: 30, horizontal: 7},
        styles: {overflow: 'linebreak',columnWidth: 'wrap', fontSize: 8},
        columnStyles: {text: {columnWidth: 'auto'}},
      });
      

      let second = doc.autoTable.previous;

      doc.text("Referencias", 10, second.finalY + 10);
      doc.setFont('helvetica')

      doc.autoTable(referencias.columns, referencias.data, {
        
        startY: second.finalY + 15,
        margin: {top: 30, horizontal: 7},
        styles: {overflow: 'linebreak',columnWidth: 'wrap', fontSize: 8},
        columnStyles: {text: {columnWidth: 'auto'}},
      });

      let third = doc.autoTable.previous;

      doc.text("Alta", 10, third.finalY + 10);
      doc.setFont('helvetica')

      doc.autoTable(altas.columns, altas.data, {
        
        startY: third.finalY + 15,
        margin: {top: 30, horizontal: 7},
        styles: {overflow: 'linebreak',columnWidth: 'wrap', fontSize: 8},
        columnStyles: {text: {columnWidth: 'auto'}},
        addPageContent: pageContent
      });



      if (typeof doc.putTotalPages === 'function') {
        doc.putTotalPages(totalPagesExp);
      }

    
      doc.save('Información de la Paciente: '+'.pdf');
      this.datosSeguimiento = [];
  }
      
  }
